import React from 'react'
import Quote from './Quote'
import Author from './Author'

class AuthorContainer extends React.Component{

    arrayOfComponents=this.props.authors.map((singularAuthor)=>{
            return <div>
                <Author key={singularAuthor.id} author={singularAuthor.authorName}/>
                <Quote key={singularAuthor.authorName} quote={singularAuthor.quote}/>
            </div>
   })

    render(){
        return(
            <ul>
                {this.arrayOfComponents}
            </ul>
        )
    }
}

export default AuthorContainer
