import React from 'react'

class Form extends React.Component{

    // Controlled Components:
    // state controls input and input controlling the state

    state={
        authorName:" ",
        quote:" ",
        motivationRate: 0
    }

    handleFormInput=(evt)=>{
        console.log(evt.target.name)
      this.setState({
          [evt.target.name]:evt.target.value.authorName,
          quote:evt.target.value.quote,
          motivationRate:evt.target.value.motivationRate
      })
    }

    render(){
        return(
            <>
            <h5>Enter a New Motivational Quote</h5>
             <form>
                 <label htmlFor="authorName">Author Name:</label>
                 <input type="text" id="authorName" 
                 value={this.state.authorName}
                 onChange={this.handleFormInput}
                 name="authorName"
                 />
                 <label htmlFor="quote">Quote:</label>
                 <input type="text" id="quote" 
                 value={this.state.quote}
                 onChange={this.handleFormInput}
                 name="quote"
                 />
                 <label htmlFor="motivationRate">Motivation Rate:</label>
                 <input type="number" id="motivationRate" 
                 value={this.state.motivationRate}
                 onChange={this.handleFormInput}
                 name="motivationRate"
                 />
                 <input type="submit" value="Add A Quote"/>
             </form>
            </>
        )
    }
}

export default Form
