    render(){   
        console.log(this.props.author)     
        return (
            <div className="container">
                <img src="https://images.unsplash.com/photo-1528716321680-815a8cdb8cbe?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=710&q=80" alt="a small board and tree"/>
                <p>Author: {this.props.author}</p>
                <button className="motivated-button" onClick={this.handleClick}>I am motivated! <span>{this.state.motivationRate}</span></button> 
                <Quote key={this.props.author} quote={this.props.quote}/>
            </div>
        )
    }
}

